import { Router, useRouter } from "next/router";
import { useEffect } from "react";
import { MdClose } from "react-icons/md";

const SearchOverlay = ({ activeStatus, getActiveStatus,  onSubmitSearchAllHandle, onSearchAllChange }) => {
  const router = useRouter()

  useEffect(() => {
    router.push('/shop')
  }, [onSubmitSearchAllHandle])
  const removeLayoutOnSubmit = (e) => {
    e.preventDefault()
    onSubmitSearchAllHandle()
    getActiveStatus(false);
    document.querySelector("body").classList.remove("overflow-hidden");
  }
  return (
    <div className={`search-overlay ${activeStatus ? "active" : ""}`}>
      {/*=======  close icon  =======*/}
      <button
        className="search-overlay__close-icon"
        onClick={() => {
          getActiveStatus(false);
          document.querySelector("body").classList.remove("overflow-hidden");
        }}
      >
        <MdClose />
      </button>
      {/*=======  End of close icon  =======*/}
      {/*=======  search overlay content  =======*/}
      <div className="search-overlay__content">
        <form className="space-mb--20" onSubmit={removeLayoutOnSubmit}>
          <input type="search" placeholder="Search Products..." onChange={onSearchAllChange}/>
        </form>
        <div className="search-overlay__hint"># Hit enter to search</div>
      </div>
      {/*=======  End of search overlay content  =======*/}
    </div>
  );
};

export default SearchOverlay;
