import { Fragment, useEffect } from "react";
import Link from "next/link";
import { IoIosSearch } from "react-icons/io";
import {
  getIndividualBrands,
  getIndividualColors,
  setActiveSort,
  // getProducts,
  getDiscountPrice
} from "../../lib/product";
import { ProductRating } from "../Product";
import { useRouter } from 'next/router'


const ShopSidebar = ({ products, getSortParams, onSearchChange, onSubmitHandle }) => {
  const categories = getIndividualBrands(products);
  const router = useRouter()
  const {path} = router.query


  useEffect(() => {
    getSortParams("category", "");
  }, [path] )

  return (
    <div className="shop-sidebar">
      <div className="single-sidebar-widget space-mb--40">
        {/* search widget */}
        <div className="search-widget">
          <form onSubmit={onSubmitHandle}>
            <input 
            type="search" 
            placeholder="Search products ..." 
            onChange={onSearchChange}
            />
            <button type="button">
              <IoIosSearch />
            </button>
          </form>
        </div>
      </div>

      {/* category list */}
      <div className="single-sidebar-widget space-mb--40">
        <h2 className="single-sidebar-widget__title space-mb--30">
          Brands
        </h2>
        {categories.length > 0 ? (
          <ul className="single-sidebar-widget__list single-sidebar-widget__list--category">
            <li>
              <button
                onClick={(e) => {
                  getSortParams("category", "");
                  setActiveSort(e);
                }}
                className="active"
              >
                All brands
              </button>
            </li>
            {categories.map((category, i) => {
              return (
                <li key={i}>
                  <button
                    onClick={(e) => {
                      getSortParams("category", category);
                      setActiveSort(e);
                    }}
                  >
                    {category}
                  </button>
                </li>
              );
            })}
          </ul>
        ) : (
          "No brands found"
        )}
      </div>

    </div>
  );
};

export default ShopSidebar;
