import axiosInstance from "../../helpers/axios"
import { productConstants } from "./constants"

export const getProductsBySlug = (slug) => {
    return async dispatch => {
        const res = await axiosInstance.get(`/products/${slug}?name=`)
        if(res.status === 200){
            dispatch({
                type: productConstants.GET_PRODUCTS_BY_SLUG,
                payload: res.data
            })
        }
    }
}

export const getAllProducts = () => {
    return async dispatch => {
        const res = await axiosInstance.get(`/products`)
        if(res.status === 200){
            dispatch({
                type: productConstants.GET_ALL_PRODUCTS_FROM_PRODUCTS,
                payload: res.data
            })
        }
    }
}

export const getCategoryProductBySearch = (slug, search) => {
    return async dispatch => {
        const res = await axiosInstance.get(`/products/${slug}?name=${search}`)
        if(res.status === 200){
            dispatch({
                type: productConstants.GET_CATEGORY_PRODUCTS_BY_SEARCH,
                payload: res.data
            })
        }
    }
}

export const getAllProductBySearch = (search) => {
    return async dispatch => {
        const res = await axiosInstance.get(`/allproducts?name=${search}`)
        if(res.status === 200){
            dispatch({
                type: productConstants.GET_ALL_PRODUCTS_BY_SEARCH,
                payload: res.data
            })
        }
    }
}
