import axios from 'axios'
import { api } from '../urlConfig'
import React from 'react'



const axiosInstance = () => {
    const token = localStorage.getItem('token')
    return (
        axios.create({
            baseURL: api,
            headers: {
                'Authorization': token ? `Bearer ${token}` : ''
            }
        
        })
    )
} 

export default axiosInstance
